                |import isolette.Regulate._
                |
                |class ${suiteName} extends Manage_Regulator_Interface_impl_thermostat_regulate_temperature_manage_regulator_interface_GumboX_TestHarness_ScalaTest {
                |  val verbose: B = true
                |
