  def seedGen: Gen64 = Random.Gen64Impl(Xoshiro256.create)

  def getProfiles_P: ISZ[OperatorInterface_s_tcproc_operatorInterface_Profile_P]

  def getDefaultProfile_P: OperatorInterface_s_tcproc_operatorInterface_Profile_P = {
    return OperatorInterface_s_tcproc_operatorInterface_Profile_P (
      name = "Default Port Profile", 
      numTests = 100, 
      api_tempChanged = RandomLib(Random.Gen64Impl(Xoshiro256.createSeed(seedGen.genU64()))), 
      api_currentTemp = RandomLib(Random.Gen64Impl(Xoshiro256.createSeed(seedGen.genU64()))))
  }

  def next(profile: OperatorInterface_s_tcproc_operatorInterface_Profile_P): Option[OperatorInterface_s_tcproc_operatorInterface_PreState_Container_P] = {
    try {
      val api_tempChanged = profile.api_tempChanged.nextOption_artEmpty()
      val api_currentTemp = profile.api_currentTemp.nextTempSensorTemperature_i()

      return Some(OperatorInterface_s_tcproc_operatorInterface_PreState_Container_P(api_tempChanged,api_currentTemp))
    } catch {
      case e: AssertionError =>
       // SlangCheck was unable to satisfy a datatype's filter
       return None()
    }
  }

  def numInitialiseTests: Z = 100

  {
    for (i <- 0 to numInitialiseTests) {
      this.registerTest(s"testInitialiseCB_$i") {
        testInitialiseCB() match {
          case GumboXResult.Pre_Condition_Unsat =>
            halt("Infeasible as initialize entry points cannot contain assume clauses and cannot access incoming ports or state variables")
          case GumboXResult.Post_Condition_Fail =>
            fail ("Post condition did not hold")
          case GumboXResult.Post_Condition_Pass =>
            if (verbose) {
              println ("Success!")
            }
        }
      }
    }
  }

  for (profile <- getProfiles_P) {
    testComputeCB_Profile_P(profile)
  }

  def testComputeCB_Profile_P(profile: OperatorInterface_s_tcproc_operatorInterface_Profile_P): Unit = {
    for (i <- 0 to profile.numTests) {
      this.registerTest(s"Profile \"${profile.name}\": testComputeCB_$i") {
        var retry: B = T

        var j: Z = 0
        while (j < GumboXUtil.numTestVectorGenRetries && retry) {
          next(profile) match {
            case Some(o) =>

              if (verbose && j > 0) {
                println(s"Retry $j:")
              }

              val results = testComputeCBV(o)

              if (verbose) {
                val tq = "\"\"\""
                println(st"""Replay Unit Test:
                            |  test("Profile \"${profile.name}\": testComputeCB_$i") {
                            |    val json = st${tq}${tc.JSON.fromTempControlSoftwareSystemOperatorInterface_s_tcproc_operatorInterface_PreState_Container_P(o, T)}${tq}.render
                            |    val testVector = tc.JSON.toTempControlSoftwareSystemOperatorInterface_s_tcproc_operatorInterface_PreState_Container_P(json).left
                            |    assert (testComputeCBV(testVector) == tc.GumboXUtil.GumboXResult.$results)
                            |  }""".render)
              }

              results match {
                case GumboXResult.Pre_Condition_Unsat =>
                case GumboXResult.Post_Condition_Fail =>
                  fail ("Post condition did not hold")
                  retry = F
                case GumboXResult.Post_Condition_Pass =>
                  if (verbose) {
                    println ("Success!")
                  }
                  retry = F
              }
            case _ =>
          }
          j = j + 1
        }

        if (retry) {
          if (failOnUnsatPreconditions) {
            fail ("Unable to satisfy precondition")
          } else if (verbose) {
            cprintln(T, "Unable to satisfy precondition")
          }
        }
      }
    }
  }
