object TempControl_s_tcproc_tempControl_SystemTestAPI {
  /** helper method to set the values of all incoming ports and state variables
    * @param In_currentFanState pre-state state variable
    * @param In_currentSetPoint pre-state state variable
    * @param In_latestTemp pre-state state variable
    * @param api_tempChanged incoming event port
    * @param api_fanAck incoming event data port
    * @param api_setPoint incoming event data port
    * @param api_currentTemp incoming data port
    */
  def put_concrete_inputs(In_currentFanState: CoolingFan.FanCmd.Type,
                          In_currentSetPoint: TempControlSoftwareSystem.SetPoint_i,
                          In_latestTemp: TempSensor.Temperature_i,
                          api_tempChanged: Option[art.Empty],
                          api_fanAck: Option[CoolingFan.FanAck.Type],
                          api_setPoint: Option[TempControlSoftwareSystem.SetPoint_i],
                          api_currentTemp: TempSensor.Temperature_i): Unit = {
    put_In_currentFanState(In_currentFanState)
    put_In_currentSetPoint(In_currentSetPoint)
    put_In_latestTemp(In_latestTemp)
    put_tempChanged(api_tempChanged)
    put_fanAck(api_fanAck)
    put_setPoint(api_setPoint)
    put_currentTemp(api_currentTemp)
  }

  // setter for state variable
  def put_In_currentFanState(value: CoolingFan.FanCmd.Type): Unit = {
    TempControl_s_tcproc_tempControl.currentFanState = value
  }

  // setter for state variable
  def put_In_currentSetPoint(value: TempControlSoftwareSystem.SetPoint_i): Unit = {
    TempControl_s_tcproc_tempControl.currentSetPoint = value
  }

  // setter for state variable
  def put_In_latestTemp(value: TempSensor.Temperature_i): Unit = {
    TempControl_s_tcproc_tempControl.latestTemp = value
  }

  // setter for incoming event  port
  def put_tempChanged(value: Option[art.Empty]): Unit = {
    value match {
      case Some(v) => Art.insertInInfrastructurePort(Arch.TempControlSoftwareSystem_s_Instance_tcproc_tempControl.operational_api.tempChanged_Id, v)
      case _ =>
    }
  }

  // setter for incoming event data port
  def put_fanAck(value: Option[CoolingFan.FanAck.Type]): Unit = {
    value match {
      case Some(v) => Art.insertInInfrastructurePort(Arch.TempControlSoftwareSystem_s_Instance_tcproc_tempControl.operational_api.fanAck_Id, CoolingFan.FanAck_Payload(v))
      case _ =>
    }
  }

  // setter for incoming event data port
  def put_setPoint(value: Option[TempControlSoftwareSystem.SetPoint_i]): Unit = {
    value match {
      case Some(v) => Art.insertInInfrastructurePort(Arch.TempControlSoftwareSystem_s_Instance_tcproc_tempControl.operational_api.setPoint_Id, TempControlSoftwareSystem.SetPoint_i_Payload(v))
      case _ =>
    }
  }

  // setter for incoming data port
  def put_currentTemp(value: TempSensor.Temperature_i): Unit = {
    Art.insertInInfrastructurePort(Arch.TempControlSoftwareSystem_s_Instance_tcproc_tempControl.operational_api.currentTemp_Id, TempSensor.Temperature_i_Payload(value))
  }

  def fetchContainer(): tc.TempControlSoftwareSystem.TempControl_s_tcproc_tempControl_PostState_Container_PS = {
    if (runtimeMonitorStream.contains(Arch.TempControlSoftwareSystem_s_Instance_tcproc_tempControl.id)) {
      val (_, postContainer_) = runtimeMonitorStream.get(Arch.TempControlSoftwareSystem_s_Instance_tcproc_tempControl.id).get
      return postContainer_.asInstanceOf[tc.TempControlSoftwareSystem.TempControl_s_tcproc_tempControl_PostState_Container_PS]
    }
    else {
      assert(F, s"No post state recorded for ${Arch.TempControlSoftwareSystem_s_Instance_tcproc_tempControl.name}")
      halt(s"No post state recorded for ${Arch.TempControlSoftwareSystem_s_Instance_tcproc_tempControl.name}")
    }
  }

  def check_concrete_outputs(currentFanState: CoolingFan.FanCmd.Type,
                             currentSetPoint: TempControlSoftwareSystem.SetPoint_i,
                             latestTemp: TempSensor.Temperature_i,
                             api_fanCmd: Option[CoolingFan.FanCmd.Type]): Unit = {
    var failureReasons: ISZ[ST] = ISZ()

    val actual_currentFanState = get_currentFanState()
    if (currentFanState != actual_currentFanState) {
      failureReasons = failureReasons :+ st"'currentFanState' did not match expected.  Expected: $currentFanState, Actual: $actual_currentFanState"
    }
    val actual_currentSetPoint = get_currentSetPoint()
    if (currentSetPoint != actual_currentSetPoint) {
      failureReasons = failureReasons :+ st"'currentSetPoint' did not match expected.  Expected: $currentSetPoint, Actual: $actual_currentSetPoint"
    }
    val actual_latestTemp = get_latestTemp()
    if (latestTemp != actual_latestTemp) {
      failureReasons = failureReasons :+ st"'latestTemp' did not match expected.  Expected: $latestTemp, Actual: $actual_latestTemp"
    }
    val actual_fanCmd = get_api_fanCmd()
    if (api_fanCmd != actual_fanCmd) {
      failureReasons = failureReasons :+ st"'fanCmd' did not match expected.  Expected: $api_fanCmd, Actual: $actual_fanCmd"
    }

    assert(failureReasons.isEmpty, st"${(failureReasons, "\n")}".render)
  }

  // getter for state variable
  def get_currentFanState(): CoolingFan.FanCmd.Type = {
    return TempControl_s_tcproc_tempControl.currentFanState
  }

  // getter for state variable
  def get_currentSetPoint(): TempControlSoftwareSystem.SetPoint_i = {
    return TempControl_s_tcproc_tempControl.currentSetPoint
  }

  // getter for state variable
  def get_latestTemp(): TempSensor.Temperature_i = {
    return TempControl_s_tcproc_tempControl.latestTemp
  }

  def get_api_fanCmd(): Option[CoolingFan.FanCmd.Type] = {
    return fetchContainer().api_fanCmd
  }
}